/**
 * Quote a value.
 *
 * @param value
 *   Value(s) to wrap in quotes
 * @param [open='"']
 *   Opening quote
 * @param [close=open]
 *   Closing quote
 */
export const quotation: {
  (value: string, open?: string | undefined, close?: string | undefined): string
  (
    value: string[],
    open?: string | undefined,
    close?: string | undefined
  ): string[]
}
